scheduledQueryRules.conditionQuery:
let PerfTimeWindow = 60m;
let DefaultTotalEgressThresholdInMB = 10 * 1024;
let RuleName = 'Azure Blob Storage Total Egress High';
let LimitName = 'TotalEgressThresholdInMB';
AzureMetrics
| where MetricName == 'Egress' and TimeGenerated > ago(PerfTimeWindow)
| summarize TimeGenerated = max(TimeGenerated), EgressBytes = avg(Average) by Resource, ResourceId
| extend EgressMegabytes = tolong(round(EgressBytes / 1048576.0))
| project TimeGenerated, ResourceId, Resource, EgressBytes, EgressMegabytes, EgressGB = round(EgressMegabytes / 1024.0)
// output: TimeGenerated, ResourceId, Resource, EgressBytes, EgressMegabytes, EgressG

let PerfTimeWindow = 10m;
//let DefaultStoragePercentageLimit = 80.0;
AzureMetrics
| where MetricName == 'storage_percent' and TimeGenerated > ago(PerfTimeWindow)
| summarize StoragePercent = arg_max(Maximum,TimeGenerated) by ResourceId, Resource
| project TimeGenerated, ResourceId, Resource, StoragePercent
//| where StoragePercent > DefaultStoragePercentageLimit
// output: TimeGenerated, ResourceId, Resource, StorageGigabytes, StoragePercent, Storage, StoragePercentLimit
